<template>
  <div class="container">
    <!-- 좌측: 식량 패널 -->
    <section class="panel left">
      <h2>식량</h2>

      <ul class="list">
        <li v-for="food in foods" :key="food.id">
          <label>
            <input type="checkbox" :value="food.id" v-model="selectedFoods" :disabled="food.qty <= 0" />
            {{ food.name }}
            <span class="qty"> (남은 수량: {{ food.qty }})</span>
          </label>
        </li>
      </ul>

      <button class="send-btn" @click="sendFoods">전달</button>
    </section>

    <!-- 우측: 사람 목록 -->
    <section class="panel right">
      <h2>백성</h2>

      <ul class="people-list">
        <li v-for="person in people" :key="person.id" class="person-card">
          <div class="person-row">
            <label class="avatar-label">
              <input class="radio" type="radio" name="person" :value="person.id" v-model="selectedPerson" />
              <img class="avatar" :src="person.avatar" :alt="person.name" draggable="false" />
            </label>

            <div class="received-inline">
              <span class="who">{{ person.name }}</span>
              <span class="sep"> : </span>
              <span class="what">
                {{ formatFoods(person.receivedFoodNames) }}
              </span>
            </div>
          </div>
        </li>
      </ul>
    </section>
  </div>
</template>

<script setup>
import { ref } from 'vue'

// foods (두쫀쿠 포함)
const foods = ref([
  { id: 1, name: '두쫀쿠', qty: 2 },
  { id: 2, name: '라면', qty: 3 },
  { id: 3, name: '불닭볶음면', qty: 1 },
  { id: 4, name: '생수', qty: 2 },
  { id: 5, name: '스팸', qty: 1 },
  { id: 6, name: '쌀', qty: 2 },
  { id: 7, name: '뿌링클', qty: 1 },
  { id: 8, name: '왕갈비통닭', qty: 1 },
])

// ✅ people에 avatar(base64 data URL) 추가
const people = ref([
  {
    id: 'u1',
    name: '똘똘이 스머프',
    avatar:
      'data:image/jpeg;base64,',
    receivedFoodNames: [],
  },
  {
    id: 'u2',
    name: '파파 스머프',
    avatar:
      'data:image/png;base64,',
    receivedFoodNames: [],
  },
  {
    id: 'u3',
    name: '가가멜',
    avatar:
      'data:image/jpeg;base64,',
    receivedFoodNames: [],
  },
])

const selectedFoods = ref([])
const selectedPerson = ref(null)

const sendFoods = () => {
  if (!selectedPerson.value) {
    alert('받을 백성을 선택해 주세요.')
    return
  }
  if (selectedFoods.value.length === 0) {
    alert('전달할 식량을 1개 이상 선택해 주세요.')
    return
  }

  const receiver = people.value.find((p) => p.id === selectedPerson.value)
  if (!receiver) return

  selectedFoods.value.forEach((foodId) => {
    const targetFood = foods.value.find((f) => f.id === foodId)
    if (!targetFood) return
    if (targetFood.qty > 0) {
      receiver.receivedFoodNames.push(targetFood.name)
      targetFood.qty -= 1
    }
  })

  foods.value = foods.value.filter((f) => f.qty > 0)
  selectedFoods.value = []
}

// 전달된 음식 배열을 "라면(2), 두쫀쿠(1)" 형태로 변환
const formatFoods = (foodNames) => {
  if (!foodNames || foodNames.length === 0) return '(아직 받은 식량 없음)'

  const counter = {}

  foodNames.forEach((name) => {
    counter[name] = (counter[name] || 0) + 1
  })

  return Object.entries(counter)
    .map(([name, count]) =>
      count > 1 ? `${name}(${count})` : name
    )
    .join(', ')
}

</script>

